#include"stdio.h"  
main()  
{char c;int i=0,j=0,k=0,l=0;  
while((c=getchar())!='\n')  
{if(c>=65&&c<=90||c>=97&&c<=122) i++;  
else if(c>=48&&c<=57) j++;  
else if(c==32) k++;  
else l++;}  
printf("%d %d %d %d",i,j,k,l);  
}

#include<stdio.h>
int main(){
  int i,n;
  float s=0.0;
  float fact(int n);
  do{
     scanf("%d",&n);
  }while(n<0);
  for(i=1;i<=n;i++)
	s+=1/fact(i);
  printf("%.4f",s);

}
float fact(int n){
  int i;
  float f=1.0;
  for(i=1;i<=n;i++)
     f*=i;
  return f;
  
}
